import React, { useState } from "react";

const ProductCart = ({ cartItems }) => {
  const [cartProducts, setCartProducts] = useState(cartItems);
  const [total, setTotal] = useState(0); // Biến total để tính tổng giá trị các sản phẩm trong giỏ hàng

  // Tính toán tổng giá trị của các sản phẩm trong giỏ hàng
  const totalAmount = cartProducts.reduce(
    (total, item) => total + item.price * item.count,
    0
  );

  const handleRemoveFromCart = (index) => {
    const updatedCart = [...cartProducts];
    const removedItem = updatedCart[index];
    updatedCart.splice(index, 1);

    if (removedItem) {
      setTotal(total - removedItem.price * removedItem.count);
      setCartProducts(updatedCart);
    }
  };

  const handleIncreaseQuantity = (index) => {
    const updatedCart = [...cartProducts];
    updatedCart[index].count++;

    setTotal(total + updatedCart[index].price);
    setCartProducts(updatedCart);
  };

  const handleDecreaseQuantity = (index) => {
    const updatedCart = [...cartProducts];
    updatedCart[index].count--;

    if (updatedCart[index].count === 0) {
      handleRemoveFromCart(index);
    } else {
      setTotal(total - updatedCart[index].price);
      setCartProducts(updatedCart);
    }
  };

  return (
    <div className="product-card-container">
      <div className="product-title">
        Your Cart <span style={{ float: "right" }}>${totalAmount}</span>
      </div>
      <div className="product-body">
        {cartProducts.length === 0 ? (
          <div className="cart-empty" style={{ fontSize: "14px" }}>
            Your Cart is empty
          </div>
        ) : (
          <div>
            {cartProducts.map((item, index) => (
              <div className="cart-item" key={index}>
                <div className="cart-item-left">
                  <div
                    className="cart-item-img"
                    style={{ background: item.color }}
                  >
                    <div className="cart-item-image">
                      <img src={item.image} alt="#" />
                    </div>
                  </div>
                </div>
                <div className="cart-item-right">
                  <div className="cart-item-name">{item.name}</div>
                  <div className="cart-item-price">${item.price}</div>
                  <div className="cart-item-action">
                    <div
                      className="cart-item-increase"
                      onClick={() => handleDecreaseQuantity(index)}
                    >
                      <div className="cart-item-button">-</div>
                    </div>
                    <div className="cart-item-count">{item.count}</div>
                    <div
                      className="cart-item-increase"
                      onClick={() => handleIncreaseQuantity(index)}
                    >
                      <div className="cart-item-button">+</div>
                    </div>
                    <div
                      className="cart-item-remove"
                      onClick={() => handleRemoveFromCart(index)}
                    >
                      <img src="/image/trash.png" alt="#" />
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductCart;
