import React, { useState } from "react";
const ProductCart = ({ cartItems }) => {
  const [count, setCount] = useState(1); // Biến count để đếm số lượng sản phẩm
  const [total, setTotal] = useState(0); // Biến total để tính tổng giá trị các sản phẩm trong giỏ hàng

  const addToCart = (item) => {
    const newCount = count + 1; // Tăng count lên khi thêm vào giỏ hàng
    setCount(newCount);

    const newTotal = total + item.price; // Cộng giá của sản phẩm vào total
    setTotal(newTotal);

    // Code xử lý thêm sản phẩm vào giỏ hàng ở đây...
  };
  return (
    <div className="product-card-container">
      <div className="product-logo">
        <img src="/image/nike.png" alt="#" />
      </div>
      <div className="product-title">
        Your Cart <span style={{ float: "right" }}>{total}</span>
      </div>
      <div className="product-body">
        {cartItems.length === 0 ? (
          <div className="cart-empty" style={{ fontSize: "14px" }}>
            Your Cart is empty
          </div>
        ) : (
          <div>
            {cartItems.map((item, index) => (
              <div className="cart-item" key={index}>
                <div className="cart-item-left">
                  <div
                    className="cart-item-img"
                    style={{ background: item.color }}
                  >
                    <div className="cart-item-image">
                      <img src={item.image} alt="#" />
                    </div>
                  </div>
                </div>
                <div className="cart-item-right">
                  <div className="cart-item-name">{item.name}</div>
                  <div className="cart-item-price">${item.price}</div>
                  <div className="cart-item-action">
                    <div className="cart-item-increase">
                      <div className="cart-item-button">-</div>
                      <div className="cart-item-count">{count}</div>
                      <div className="cart-item-button">+</div>
                    </div>
                    <div className="cart-item-remove">
                      <img src="/image/trash.png" alt="#" />
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductCart;
